    public boolean validateData()
    {
        // EMail Validation Code
        String emailAddress = null;
        String firstNameID = null;
        String lastNameID = null;
        String streetAddr = null;
        String cityAddr = null;
        String zipAddr = null;
        String cc1ID = null;
        String cc2ID = null;        
        String cc3ID = null;
        String cc4ID = null;  
        String verifCode = null;
        try
        {
            firstNameID=getFirstName();
            if(firstNameID.equals(""))
            {
                JOptionPane.showMessageDialog(null, EncryptedRuleReader.get("paymentFirstNameDataFieldEmptyMessage"));
                tfFirstName.grabFocus();       
                return false;
            }
        }
        catch(NullPointerException e)
        {
            JOptionPane.showMessageDialog(null,EncryptedRuleReader.get("paymentFirstNameDataFieldEmptyMessage"));
            tfFirstName.grabFocus();
            return false;           
        }
        try
        {
            lastNameID=getLastName();
            if(lastNameID.equals(""))
            {
                JOptionPane.showMessageDialog(null,EncryptedRuleReader.get("paymentLastNameDataFieldEmptyMessage"));
                tfLastName.grabFocus();         
                return false;                
            }
        }
        catch(NullPointerException e)
        {
            JOptionPane.showMessageDialog(null,EncryptedRuleReader.get("paymentLastNameDataFieldEmptyMessage"));
            tfLastName.grabFocus();
            return false;            
        }        
        try
        {
            emailAddress = getEMailName();
            if(emailAddress.indexOf("@") == -1)
            {
                JOptionPane.showMessageDialog(null,EncryptedRuleReader.get("paymentValidEMailDataFieldEmptyMessage"));
                tfEMail.grabFocus();
                return false;                
            }
            else if(emailAddress.indexOf(".") == -1)
            {
                JOptionPane.showMessageDialog(null,EncryptedRuleReader.get("paymentValidEMailDataFieldEmptyMessage"));
                tfEMail.grabFocus();
                return false;                
            }
        }
        catch(NullPointerException e)
        {
            JOptionPane.showMessageDialog(null,EncryptedRuleReader.get("paymentEMailDataFieldEmptyMessage"));
            tfEMail.grabFocus();
            return false;            
        }
        try
        {
            streetAddr=getAddress();
            if(streetAddr.equals(""))
            {
                JOptionPane.showMessageDialog(null,EncryptedRuleReader.get("paymentStreetDataFieldEmptyMessage"));
                tfAddress.grabFocus();  
                return false;                
            }            
        }
        catch(NullPointerException e)
        {
            JOptionPane.showMessageDialog(null,EncryptedRuleReader.get("paymentStreetDataFieldEmptyMessage"));
            tfAddress.grabFocus();
            return false;            
        }
        try
        {
            cityAddr=getCity();
            if(cityAddr.equals(""))
            {
                JOptionPane.showMessageDialog(null,EncryptedRuleReader.get("paymentCityDataFieldEmptyMessage"));
                tfCity.grabFocus();
                return false;                
            }                
        }
        catch(NullPointerException e)
        {
            JOptionPane.showMessageDialog(null,EncryptedRuleReader.get("paymentCityDataFieldEmptyMessage"));
            tfCity.grabFocus();
            return false;            
        }
        try
        {
            zipAddr=getZip();
            if(zipAddr.equals(""))
            {
                JOptionPane.showMessageDialog(null,EncryptedRuleReader.get("paymentZipDataFieldEmptyMessage"));
                tfZipCode.grabFocus();  
                return false;                
            }                  
        }
        catch(NullPointerException e)
        {
            JOptionPane.showMessageDialog(null,EncryptedRuleReader.get("paymentZipDataFieldEmptyMessage"));
            tfZipCode.grabFocus();
            return false;            
        }        
        try
        {
            cc1ID=getCC1();
            cc2ID=getCC2();
            cc3ID=getCC3();
            cc4ID=getCC4();
            if(cc1ID.equals("")==true || cc2ID.equals("")==true || cc3ID.equals("")==true || cc4ID.equals("")==true)
            {
                JOptionPane.showMessageDialog(null,EncryptedRuleReader.get("paymentCCDataFieldEmptyMessage"));
                tfCC1.grabFocus();   
                return false;                
            }                    
        }
        catch(NullPointerException e)
        {
            JOptionPane.showMessageDialog(null,EncryptedRuleReader.get("paymentCCDataFieldEmptyMessage"));
            tfCC1.grabFocus();
            return false;            
        }        
        
        try
        {
            if(getVerificationCodeObjectIsVisible()==true)
            {
                verifCode=getVerificationCode();
                if(verifCode.equals(""))
                {
                    JOptionPane.showMessageDialog(null,EncryptedRuleReader.get("paymentCCVerificationCodeFieldEmptyMessage"));
                    tfVerificationCode.grabFocus();  
                    return false;                    
                }                  
            }
        }
        catch(NullPointerException e)
        {
            JOptionPane.showMessageDialog(null,EncryptedRuleReader.get("paymentCCVerificationCodeFieldEmptyMessage"));
            tfVerificationCode.grabFocus();
            return false;           
        }        
        String formRequestMethod = null;
        if(EncryptedRuleReader.get("formRequestMethod") != null)
        {
            formRequestMethod = EncryptedRuleReader.get("formRequestMethod");
        }      
        else
        {
             return false;
        }
        return true;
    }
